  }

  body::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      radial-gradient(circle at 20% 20%, hsla(0 0% 100% / 0.1) 2px, transparent 2px),
      radial-gradient(circle at 60% 40%, hsla(0 0% 100% / 0.05) 1px, transparent 1px),
      radial-gradient(circle at 80% 70%, hsla(0 0% 100% / 0.08) 1.5px, transparent 1.5px);
    background-size: 100px 100px, 60px 60px, 80px 80px;
    pointer-events: none;
    z-index: 0;
  }
}

@layer components {
  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(20px);
    border: 1px solid var(--glass-border);
    box-shadow: 0 8px 32px hsla(0 0% 0% / 0.1);
  }

  .nav-item {
    transition: all 0.3s ease;
  }

  .nav-item:hover {
    background: hsla(0 0% 100% / 0.2);
    transform: translateX(5px);
  }

  .orbital-ring {
    border: 2px dashed var(--orbital-ring);
    border-radius: 50%;
    position: absolute;
    animation: rotate 30s linear infinite;
  }

  .orbital-element {
    position: absolute;
    animation: counter-rotate 30s linear infinite;
  }

  .robot-float {
    animation: float 3s ease-in-out infinite;
  }

  .mood-emoji {
    transition: all 0.3s ease;
    cursor: pointer;
  }

  .mood-emoji:hover {
    transform: scale(1.2);
  }

  .game-card {
    transition: all 0.3s ease;
  }

  .game-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 40px hsla(0 0% 0% / 0.15);
  }
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes counter-rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(-360deg); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

